class Hymns {
  String? author;
  int? favorite;
  int id;
  String lyric;
  String title;
  String? tune;

  Hymns(
      {this.author,
      this.favorite,
      required this.id,
      required this.lyric,
      required this.title,
      this.tune});

  factory Hymns.fromJson(Map<String, dynamic> json) {
    return Hymns(
      author: json['author'],
      favorite: json['favorite'],
      id: json['id'],
      lyric: json['lyric'],
      title: json['title'],
      tune: json['tune'],
    );
  }

  Map<String, dynamic> toJson() {
    final Map<String, dynamic> data = <String, dynamic>{};
    data['author'] = author;
    data['favorite'] = favorite;
    data['id'] = id;
    data['lyric'] = lyric;
    data['title'] = title;
    data['tune'] = tune;
    return data;
  }
}

